final List<dynamic> styles = ['\n.selected{\n    background-color: #CFD8DC !important;\n    color: white;\n}\n.heros{\n    margin: 0 0 2em 0;\n    list-style-type: none;\n    padding: 0;\n    width: 15em;\n}\n.heros li{\n    cursor: pointer;\n    position: relative;\n    left: 0;\n    background-color: #EEE;\n    margin: .5em;\n    padding: .3em 0;\n    height: 1.6em;\n    border-radius: 4px;\n}\n\n.heros li:hover{\n    color: #607D8B;\n    background-color: #EEE;\n    left: .1em;\n}\n.heros li.selected:hover{\n    background-color: #BBD8DC !important;\n    color: white;\n}\n.heros .text{\n    position: relative;\n    top: -3px;\n}\n.heros .badge{\n    display: inline-block;\n    font-size: small;\n    color: white;\n    padding: 0.8em .7em 0 .7em;\n    background-color: #607D8B;\n    line-height: 1em;\n    position: relative;\n    left: -1px;\n    top:  -4px;\n    height: 1.8em;\n    margin-right: .8em;\n    border-radius: 4px 0 0 4px;\n}\n\nbutton{\n    font-family: Arial;\n    background-color: #eee;\n    border: none;\n    padding: 5px 10px;\n    border-radius: 4px;\n    cursor: pointer;\n    cursor: hand;\n}\n\nbutton:hover{\n    background-color: #cfd8dc;\n}\nbutton.delete{\n    float: right;\n    margin-top: 2px;\n    margin-right: .8em;\n    background-color: grey !important;\n    color: white;\n}'];